import React, { Component } from 'react';
import { withRouter } from 'react-router-dom';

class Registerform extends Component {
    constructor(props) {
        super(props);

        this.state = {
            name: "",
            username: "",
            password: "",
            email: "",
            type: ""
        }
    }

    handleChange = (e) => {
        this.setState({
            [e.target.name]: e.target.value
        })
    }

    render() {
        return (
            <form onSubmit={(e) => this.props.handleSubmit(e, this.state)} className="registration">
                <h2>Register</h2>
                <p>Your name or your business name<br></br>
                *required*
                <input type="text"
                    name="name"
                    value={this.state.name}
                    placeholder="Your name Here!"
                    onChange={this.handleChange}
                /></p><br></br><br></br>
                <p>Pick a username!<br></br>
                *required*</p>
                <input type="text" 
                    name="username" 
                    value={this.state.username}
                    placeholder="Username"
                    onChange={this.handleChange}
                /><br></br><br></br>
                <p>Make a Password<br></br>
                *required*</p>
                <input type="password" 
                    name="password" 
                    placehold="Password"
                    value={this.state.password}
                    onChange={this.handleChange}
                /><br></br><br></br>
                <p>Contact email<br></br>
                *required*</p>
                <input type="text"
                    name="email"
                    value={this.state.email}
                    placeholder="email@email.com"
                    onChange={this.handleChange}
                /><br></br><br></br>
                <p>You must enter if this is a business or a personal account<br></br>
                *required*</p>
                <input type="text"
                    name="type"
                    value={this.state.type}
                    placeholder="Business or Personal"
                    onChange={this.handleChange}
                /><br></br><br></br>
                <input type="submit" 
                    value="Register"
                />
            
            </form>
        )
    }
}

export default withRouter(Registerform);
