import React, { Component } from 'react';
import { withRouter } from 'react-router-dom';

import { editCritter } from '../../services/apihelper';

class AnimalProfile extends Component {
    constructor(props) {
        super(props);
        
        this.state={
            animal: this.props.critter
        }
    }

    handleSubmit = async (e, id, values) => {
        e.prevenDefault();
        const editAnimal = await editCritter(this.props.critter._id, values);
        this.setState({
            animal: editAnimal
        })
        this.props.history.push(`/animal/show/${id}`)
    }
    

    render() {
        console.log(this.props.critter)
        return (
            <div>
                <h1>Hello everyone! My name is {this.props.critter.name}!</h1>
                <img src={this.props.critter.image} alt="animal" />
                <p>{this.props.critter.age}</p>
                <p>Need to change some of my information?</p>
            </div>
        )
    }
}

export default AnimalProfile;
