import React, {Component} from 'react';
import { Route, Link, withRouter } from 'react-router-dom';

//apihelper functions
import {registerUser, loginUser, verifyUser } from './services/apihelper'

//custom components
import Login from './Components/Users/Login';
import Register from './Components/Users/Register';
import PostContainer from './Components/Posts/PostContainer';
import ProfileContainer from './Components/Profiles/ProfileContainer';

class App extends Component {
  constructor(props) {
    super(props);
      this.state = {
        currentUser: null
      }
    }

handleRegister = async (e, user) => {
  e.preventDefault();
  const loadedUser = await registerUser(user);
  this.setState({
    currentUser: loadedUser
  })
}

handleLogin = async (e, user) => {
  e.preventDefault();
  const loadedUser = await loginUser(user);
  this.setState({
    currentUser: loadedUser
  })
}

handleLogout = () => {
  this.setState({
    currentUser: null
  })
  localStorage.removeItem('authToken');
}

async componentDidMount() {
  const currentUser = await verifyUser();
  if(currentUser) {
    this.setState({
      currentUser: currentUser
    })
  }
}


  render() {
    return (
      <div>
        <header>
          <nav>
              {this.state.currentUser ? 
              <button onClick={this.handleLogout}>Logout</button> : (
              <div>
                <Link to="/register">SignUp</Link>
                <Link to="/login">Login</Link>
              </div>
            )}
            {this.state.currentUser && <Link to="/profile">Profile</Link>}
            <Link to="/">Home</Link>
          </nav>
        </header>
        

        <div>
          <Route path="/register" render={() => {
            return <Register handleSubmit={this.handleRegister} />
            }}
          />
          
          <Route path="/login" render={() => {
            return <Login handleSubmit={this.handleLogin} />
            }}
          />
          
          <Route path="/profile" render={() => {
            return <div>
              {this.state.currentUser && <ProfileContainer user={this.state.currentUser} />}
            </div>
            }}
          />

          <Route exact path="/" render={() => {
            return <PostContainer />
            }}
          />
        </div>
      </div>
    )
  }
}


export default App;
