import React from 'react';

function ShowPost(props) {
    return (
            <div>
                {props.post && (
                    <div>
                        <img src={props.post.image} alt="animal" />
                        <p>{props.post.body}</p>
                        <button 
                            onClick={() => props.destroyPost(props.post._id)}>
                            Delete
                        </button>
                    </div>
                )}
            </div>
    )
}

export default ShowPost;
